import React,{useState,useEffect,useRef} from 'react'
import{makeStyles,useTheme,Button,Dialog,DialogActions,DialogContent,DialogTitle,TextField,Avatar,Tooltip,Drawer,IconButton,Hidden,CssBaseline,AppBar,Toolbar,List,Typography,Divider,ListItem} from '@material-ui/core'
import ToggleButton from '@material-ui/lab/ToggleButton';
import ToggleButtonGroup from '@material-ui/lab/ToggleButtonGroup';
import MenuIcon from '@material-ui/icons/Menu';
import ExploreIcon from '@material-ui/icons/Explore';
import Brightness4Icon from '@material-ui/icons/Brightness4';
import SportsBaseballIcon from '@material-ui/icons/SportsBaseball';
import VisibilityIcon from '@material-ui/icons/Visibility';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import AccessAlarmsIcon from '@material-ui/icons/AccessAlarms';
import ModalVideo from 'react-modal-video'
import {sat_data} from '../assets/master'
import MapView from './MapView'
import NightSkyView from './NightSkyView'
import ModelView from './ModelView'
import FPVView from './FPVView'

const drawerWidth = 240;
const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex"
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  stepperRoot: {
    width: '100%',
  },
  canvasPaper: {
    padding: theme.spacing(1),
    color: theme.palette.text.secondary,
  },
  drawer: {
    [theme.breakpoints.up('sm')]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    [theme.breakpoints.up('sm')]: {
      width: `calc(100% - ${drawerWidth}px)`,
      height:'10vh',
      marginLeft: drawerWidth,
    },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up('sm')]: {
      display: 'none',
    },
  },
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(0,0),
    height:'90vh'
  },
  root1:{
    height:'100%'
  },
  eliminationGrid : {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center"
  }
}));

var sat_codes=Object.keys(sat_data)
var tokenObject={}
function splitMulti(string, tokens){
  var str=string.toLowerCase()
        var tempChar = tokens[0];
        for(var i = 1; i < tokens.length; i++){
            str = str.split(tokens[i]).join(tempChar);
        }
        str = str.split(tempChar);
        return str;
}
sat_codes.map(code=>{
  var sat=sat_data[code]
  var tokens=''
  tokens+=code
  splitMulti(sat.type,[' ',',','.']).map(token=>{
    tokens+=token.trim()
  })
  splitMulti(sat.country_name,[' ',',','.']).map(token=>{
    tokens+=token.trim()
  })
  splitMulti(sat.launch_date,[' ',',','.']).map(token=>{
    tokens+=token.trim()
  })

  splitMulti(sat.name,[' ',',','.']).map(token=>{
    tokens+=token.trim()
  })
  splitMulti(sat.sat_name,[' ',',','.']).map(token=>{
    tokens+=token.trim()
  })
  if('isGeoStationary' in sat && sat.isGeoStationary)
    tokens+='geostationary'

  tokenObject[code]=tokens
})

//console.log(tokenObject)

//console.log(tokenObject)
const Main=props=>{
  const { window } = props;
  const classes = useStyles();
  const theme = useTheme();
  const [mobileOpen, setMobileOpen] = useState(false);
  const [mode, setMode] = useState('map');
  const [time,setTime]=useState('live')
  const [satellite,setSatellite]=useState('25544')
  const [searchFilter,setSearchFilter]=useState(sat_data)
  const [timeDialog,setTimeDialog]=useState(false)
  const [desDialog,setDesDialog]=useState(false)
  const [timeDiff,setTimeDiff]=useState(0)
  const [video,setVideo]=useState(true)

  const viewRef=useRef()
  const containerRef=useRef()
  const timeRef=useRef()


  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const handleMode = (event, newMode) => {
    if(newMode!=null)
      setMode(newMode);
  };

  const handleTime = (event, newTime) => {
    if(newTime!=null)
      setTime(newTime);
  };

  const fpv=()=>{
    setMode('fpv')
  }

  const modelSatClick=code=>{
    setSatellite(code)
    setDesDialog(true)
  }

  const gotoTimeLine=()=>{
    var timeDiffTmp=new Date(timeRef.current.value).getTime()-Date.now()
    viewRef.current.setTimeDiff(timeDiffTmp)
    setTimeDiff(timeDiffTmp)
    setTimeDialog(false)
  }

  useEffect(()=>{
    if(time=='other')
      setTimeDialog(true)
    else if(time=='live'){
      setTimeDiff(0)
      viewRef.current.setTimeDiff(0)
    }
  },[time])

  const filter=e=>{
    var keywords=splitMulti(e.target.value,[' ',',','.'])
    var filteredList={}
    var keys=Object.keys(tokenObject)
    keys.map(key=>{
      var isMatched=false
      var token=''
      keywords.map(keyword=>{
        token+=keyword
      })
      if(tokenObject[key].includes(token))
        filteredList[key]=sat_data[key]
    })
    setSearchFilter(filteredList)

  }

  useEffect(()=>{
    viewRef.current.setSatellite({
      satCode:satellite,
      data:sat_data[satellite]
    })
  },[satellite])

  const container = window !== undefined ? () => window().document.body : undefined;
  const drawer=(
    <div className='explorer'>
    <center>
      <ToggleButtonGroup
        value={mode}
        exclusive
        onChange={handleMode}
        aria-label="text alignment"
        style={{marginTop:'5px'}}
      >
          <ToggleButton value="map" aria-label="left aligned">
            <Tooltip title="Map View">
              <ExploreIcon />
            </Tooltip>
          </ToggleButton>
          <ToggleButton value="night" aria-label="centered">
            <Tooltip title="Night Sky View">
              <Brightness4Icon />
            </Tooltip>
          </ToggleButton>
          <ToggleButton value="3d" aria-label="right aligned">
            <Tooltip title="3D Model View">
              <SportsBaseballIcon />
            </Tooltip>
          </ToggleButton>
          <ToggleButton value="fpv" aria-label="justified">
            <Tooltip title="First Person View">
              <VisibilityIcon />
            </Tooltip>
          </ToggleButton>
        </ToggleButtonGroup>


        <ToggleButtonGroup
          value={time}
          exclusive
          onChange={handleTime}
          aria-label="text alignment"
          style={{marginTop:'5px'}}
        >
        <ToggleButton value="live" aria-label="left aligned">
          <Tooltip title="Live">
            <CheckCircleIcon/>
          </Tooltip>
        </ToggleButton>
        <ToggleButton onClick={()=>{setTimeDialog(true)}} value="other" aria-label="left aligned">
          <Tooltip title="Past/Future">
            <AccessAlarmsIcon/>
          </Tooltip>
        </ToggleButton>
          </ToggleButtonGroup>



        <TextField style={{marginTop:'20px'}} onChange={filter} id="outlined-basic" label="Search" variant="outlined" />
      </center>
      <List>
        {
          Object.keys(searchFilter).map((key,ind)=>{
            var sat=searchFilter[key]
            return(
              <div>
                <Divider />
                <Divider />
                <ListItem selected={key==satellite} onClick={()=>(setSatellite(key))}  button>
                  <div>
                    <Typography variant="subtitle1" gutterBottom>
                      {sat.name}
                    </Typography>
                    <Typography variant="body2" gutterBottom>
                      {sat.country_name}
                    </Typography>
                    <div style={{display:'flex'}}>
                      <Avatar alt={sat.country_name} src={sat.real_images[0]} />
                      <Avatar alt={sat.country_name} src={sat.country_flag} />
                    </div>
                    <center >
                      <Button style={{marginTop:'10px'}} onClick={()=>{setDesDialog(true)}} variant="outlined" color="primary" >
                        Details
                      </Button>
                    </center>
                  </div>
                </ListItem>
              <Divider />
              <Divider />
              </div>
            )
          })
        }
      </List>
      <Divider />
    </div>
  )

  return(
      <div className={classes.root}>
      <div style={{position:'absolute',height:'100vh',width:'100vw'}}>
        <React.Fragment>
          <ModalVideo channel='youtube' autoplay isOpen={video} videoId="jHfov2AC6ZA" onClose={()=>{setVideo(false)}}/>
        </React.Fragment>
      </div>
      <Dialog open={timeDialog} onClose={()=>{setTimeDialog(false);}} aria-labelledby="form-dialog-title">
          <DialogTitle id="form-dialog-title">Set timeline</DialogTitle>
          <DialogContent className={classes.root}>

          <TextField
            inputRef={timeRef}
            id="datetime-local"
            label="timeline starting point"
            type="datetime-local"
            defaultValue="2017-05-24T22:30"
            InputLabelProps={{
              shrink: true,
            }}
          />

          </DialogContent>
          <DialogActions>
            <Button onClick={()=>{setTimeDialog(false)}} color="secondary">
              Cancel
            </Button>
            <Button onClick={gotoTimeLine} color="primary">
              Go
            </Button>
          </DialogActions>
        </Dialog>
        <Dialog open={desDialog} onClose={()=>{setDesDialog(false);}} aria-labelledby="form-dialog-title">
            <DialogTitle id="form-dialog-title">{sat_data[satellite].sat_name}</DialogTitle>
            <DialogContent>
              <Typography variant="body">
                {sat_data[satellite].name}
                {sat_data[satellite].isGeoStationary?'(Geostationary)':''}
              </Typography>
              <Typography variant="body"><br/>
                Country : {sat_data[satellite].country_name}
              </Typography>
              <Typography variant="body"><br/>
                Type : {sat_data[satellite].type}
              </Typography>
              <Typography variant="body"><br/>
                Launch Date : {sat_data[satellite].launch_date}
              </Typography>
              <Typography variant="body"><br/>
                Mission Duration : {sat_data[satellite].mission_duration}
              </Typography>
              <Typography variant="body"><br/>
                Launch Mass : {sat_data[satellite].launch_mass}
              </Typography>
              <br/><img src={sat_data[satellite].country_flag} width='30%'/>
              {
                sat_data[satellite].real_images.map(url=>{
                  return(
                    <img style={{marginLeft:'2px'}} src={url} width='30%'/>
                  )
                })
              }
              <Typography variant="body"><br/>
                {sat_data[satellite].description}
              </Typography>
              <br/><br/>
                <b><u>Use Cases :</u></b>

              {
                sat_data[satellite].use_cases.map((usage,ind)=>{
                  return(
                    <Typography variant="body"><br/>
                      ({ind+1}){usage}<br/>
                    </Typography>
                  )
                })
              }
            </DialogContent>
            <DialogActions>
              <Button onClick={()=>{setDesDialog(false)}} color="secondary">
                Ok
              </Button>
            </DialogActions>
          </Dialog>
        <CssBaseline />
        <AppBar style={{backgroundColor:'#0090ff'}} position="fixed" className={classes.appBar}>
          <Toolbar>
            <IconButton
              color="inherit"
              aria-label="open drawer"
              edge="start"
              onClick={handleDrawerToggle}
              className={classes.menuButton}
            >
              <MenuIcon />
            </IconButton>
              <Typography variant="h6" noWrap>
                Satellight : putting light on satellites
              </Typography>
          </Toolbar>
        </AppBar>
        <nav className={classes.drawer} aria-label="mailbox folders">
          {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
          <Hidden smUp implementation="css">
            <Drawer
              container={container}
              variant="temporary"
              anchor={theme.direction === 'rtl' ? 'right' : 'left'}
              open={mobileOpen}
              onClose={handleDrawerToggle}
              classes={{
                paper: classes.drawerPaper,
              }}
              ModalProps={{
                keepMounted: true, // Better open performance on mobile.
              }}
            >
              {drawer}
            </Drawer>
          </Hidden>
          <Hidden xsDown implementation="css">
            <Drawer
              classes={{
                paper: classes.drawerPaper,
              }}
              variant="permanent"
              open
            >
              {drawer}
            </Drawer>
          </Hidden>
        </nav>
        <main className={classes.content}>
          <div style={{marginBottom:'-6px',padding:'0px'}} className={classes.toolbar} />
            <div className='container' ref={containerRef} style={{height:'100%'}}>
              {
                mode=='map'?(
                    <MapView fpv={fpv} key={[satellite,timeDiff]} ref={viewRef} satCode={satellite} data={sat_data[satellite]} timeDiff={timeDiff}/>
                ):(
                  mode=='night'?(
                      <NightSkyView fpv={fpv} key={[satellite,timeDiff]} ref={viewRef} satCode={satellite} data={sat_data[satellite]} timeDiff={timeDiff}/>
                  ):(
                    mode=='3d'?(
                        <ModelView show={modelSatClick} parent={containerRef} ref={viewRef} satCode={satellite} data={sat_data[satellite]} timeDiff={timeDiff}/>
                    ):(
                      mode=='fpv'?(
                          <FPVView ref={viewRef} satCode={satellite} data={sat_data[satellite]} timeDiff={timeDiff}/>
                      ):(
                        <br/>
                      )
                    )
                  )
                )
              }
          </div>
        </main>
      </div>


  )
}

export default Main
