import React,{useState,createRef,useRef,useEffect,forwardRef, useImperativeHandle} from 'react'
import { getSatelliteInfo } from "tle.js";

function getNormalizedCoord(coord, zoom) {
        const y = coord.y;
        let x = coord.x;
        const tileRange = 1 << zoom;
        if (y < 0 || y >= tileRange) {
          return null;
        }
        if (x < 0 || x >= tileRange) {
          x = ((x % tileRange) + tileRange) % tileRange;
        }
        return {
          x: x,
          y: y
        };
      }


const MapView=forwardRef((props,ref)=>{

  var map,marker,infoWindow,polyLine
  const initialData=getSatelliteInfo([props.data.tle_line1,props.data.tle_line2],Date.now()+props.timeDiff,0,0)
  const containerRef=useRef()


  useEffect(()=>{
    map = new window.google.maps.Map(containerRef.current, {
      center: {
        lat: initialData.lat,
        lng: initialData.lng
      },
      zoom: 5,
      streetViewControl: false,
      mapTypeControlOptions: {
        mapTypeIds: [window.google.maps.MapTypeId.ROADMAP,window.google.maps.MapTypeId.HYBRID]
      }
    });

    const icon = { url: props.data.icon_url, scaledSize: { width: 40, height: 40 } };
    let iconMarker = new window.google.maps.MarkerImage(
                      props.data.icon_url,
                      null,
                      new window.google.maps.Point(0, 0), /* origin is 0,0 */
                      new window.google.maps.Point(30, 30), /* anchor is bottom center of the scaled image */
                      new window.google.maps.Size(60, 50)
                  );

     marker = new window.google.maps.Marker({
        position: map.center,
        icon:iconMarker,
        title:"Hello World!"
    });
    marker.setMap(map);

  },[])


  useImperativeHandle(ref, () => ({

 }));



  useEffect(()=>{

  },[])

  return(
    <div ref={containerRef} style={{ width: "100%", height: "100%" }}/>
  )
})

export default MapView
