        atmosphereLayer.time = now;
        var simulatedMillisPerDay = 8000;
        var startTimeMillis = Date.now();
        function runSimulation() {
            var elapsedTimeMillis = Date.now() - startTimeMillis;
            var simulatedDays = elapsedTimeMillis / simulatedMillisPerDay;
            var millisPerDay = 24 * 3600 * 1000; // 24 hours/day * 3600 seconds/hour * 1000 milliseconds/second
            var simulatedMillis = simulatedDays * millisPerDay;
            var simulatedDate = new Date(startTimeMillis + simulatedMillis);
            atmosphereLayer.time = new Date(Date.now())
            wwd.redraw();
            requestAnimationFrame(runSimulation);
        }
        requestAnimationFrame(runSimulation);
  },[])

  useEffect(()=>{
    if(satCode) {
      posInterval = setInterval(() => {
        var currData=getSatelliteInfo(tle,Date.now()+timeDiff,0,0)
        marker.setPosition({
          lat:currData.lat,
          lng:currData.lng
        })

        var info=`<div>
            <center><span>${data.sat_name}</span></center>
            <center><img   height="30px" width='40px' src='${data.country_flag}'/></center>`+
            '<center>Lat:'+currData.lat.toFixed(3)+', Lng:'+currData.lng.toFixed(3)+'</center>'+
            '<center>Velocity:'+currData.velocity.toFixed(3)+'km/s <br/> Height:'+currData.height.toFixed(3)+'km</center>'+
            '</div>'

        infoWindow.setContent(info)

      }, 100);
      pathInterval = setInterval(() => {
        var currData=getSatelliteInfo(tle,Date.now()+timeDiff,0,0)
        poly.push(currData)
        polyLine.setPath(poly)
      }, 1000);
    } else {
      clearInterval(posInterval);
      clearInterval(pathInterval);
    }
  },[satCode])

  return(
    <canvas ref={containerRef} className='globeBg' style={{ backgroundColor:'#000000',width: "100%", height: "100%" }}/>
  )
})

export default FPVView
